const menu={opener:function(){$('#menu-btn').click(()=>{$('#menu-main-nav').toggleClass('show')})}};const lightbox={go:function(){this.block=$('#photos-lightbox');$('.photos-brick').click((e)=>{this.open(e)});$('#close-photos-lightbox').click((e)=>{this.close(e)});$(document).keydown((e)=>{if(e.keyCode==27){this.close(e)}})},open:function(e){$('img',this.block).attr('src',e.target.src);$('figcaption',this.block).html(e.target.title);this.block.show()},close:function(e){e.preventDefault();$('img',this.block).attr('src','').attr('alt','');$('figcaption',this.block).html('');this.block.hide()}};$(document).ready(()=>{menu.opener();lightbox.go()})
